$(document).ready((function(){$(".testimonials__plans").slick({arrows:!1,slidesToShow:2,slidesToScroll:2,dots:!0})}));const menuNav=document.getElementById("menu-nav"),headerLink=document.querySelectorAll(".header__link");menuNav.addEventListener("click",e=>{e.preventDefault(),headerLink.forEach(e=>e.classList.remove("active-link"));let n=e.target;n.classList.contains("header__link")&&n.classList.add("active-link")});